import { setStatusBarNetworkActivityIndicatorVisible } from "expo-status-bar";
import React, { FC } from "react";
import { View, Text, StyleSheet, SafeAreaView, FlatList, StatusBar,
    Animated, TouchableOpacity, Modal, Image } from "react-native";
import { Colors } from "react-native/Libraries/NewAppScreen";
import { Input, Button } from '../../components';

const DATA = [
    {
      nombre: 'Josue',
      cedula: '123456789',
      email: 'djflasdjkfjldsjflasj'
    },
    {
      nombre: 'Josue',
      cedula: '123456789',
      email: 'djflasdjkfjldsjflasj'
    },
    {
      nombre: 'Josue',
      cedula: '123456789',
      email: 'djflasdjkfjldsjflasj'
    },
    {
      nombre: 'Josue',
      cedula: '123456789',
      email: 'djflasdjkfjldsjflasj'
    },
  ];

const GestionDocente : FC = (props) => {
    const [visible, setVisible] = React.useState(false);

    const Item = ({nombre, cedula, email}) => (
      console.log(nombre),
    <TouchableOpacity onPress={() => { setVisible(true)}} style={style.item}>
        
      <View style={style.itemText}>
        <Text style={style.title}>Nombre: {nombre}</Text>
        <Text style={style.title}>Cédula: {cedula}</Text>
        <Text style={style.title}>E-mail: {email}</Text>
      </View>

    </TouchableOpacity>
    );

    const renderItem = ({ item }) => (
      console.log(item),
        <Item
            nombre={item.nombre}
            cedula={item.cedula}
            email={item.email}
        />
    );
     
    return (
        <View style={style.container}>
            <View style={style.containertitulo}>
                <Text style={style.titulo}>GESTION DOCENTES</Text>
            </View>
            <View style={style.btnImegContainerPlus}>
              <TouchableOpacity>
                <FontAwesome5
                />
                <Image
                  source={require('../../assets/plus.png')}
                  style={style.btnImeg}
                />
              </TouchableOpacity>
            </View>
            <View style={style.containerButton}>

                <SafeAreaView style={style.containerList}>
                    <FlatList
                        data={DATA}
                        renderItem={renderItem}
                    />
                </SafeAreaView>
            </View>
        </View>
    ) 
}

export default GestionDocente;

const style = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start',
        alignItems: 'center',
        backgroundColor: 'white'
    },
    containertitulo: {
        height: '15%',
        justifyContent: 'center',
    },
    titulo: {
        fontSize: 25,
        color : 'black',
        fontWeight: 'bold',
    },
    item: {
        backgroundColor: '#E3EDFE',
        opacity: 50,
        padding: 15,
        marginVertical: 8,
        marginHorizontal: 16,
        borderRadius: 20,
        borderWidth: 1,
        borderColor: '#1C7ED6'
    },
    itemText: {
      marginVertical: 8,
      marginHorizontal: 16,
    },
    title: {
        fontSize: 20,
    },
    containerList: {
        flex: 1,
        width: '98%',
        marginTop: StatusBar.currentHeight || 0,
    },
    modalBackGround: {
        flex: 1,
        backgroundColor: 'rgba(0,0,0,0.5)',
        justifyContent: 'center',
        alignItems: 'center',
    },
    containerButton: {
        justifyContent: 'space-evenly',
        flex: 1,
        width: '100%'
    },
    modalContainer: {
        width: '80%',
        backgroundColor: 'white',
        paddingHorizontal: 20,
        paddingVertical: 30,
        borderRadius: 20,
        elevation: 20,
    },
    header: {
        width: '100%',
        height: 40,
        alignItems: 'flex-end',
        justifyContent: 'center',
    },
    btnEdit: {
        position: 'absolute',
        bottom: 10,
        right: 10,
        shadowColor: 'black',
        shadowOffset: {width: 2, height: 2},
        shadowOpacity: 0.1
    },
    btnImeg: {
      height: 40,
      width: 40,
    },
    btnImegContainer: {
      position: 'relative',
      marginLeft: 10,
      marginTop: 5,
      left: 40,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#748FFC',
      borderRadius: 30,
      shadowColor: 'black',
      shadowOffset: {width: 4, height: 9},
      shadowOpacity: 0.1,
      elevation: 7,
      height: 60,
      width: 60,
    },
    btnImegContainerPeligro: {
      position: 'relative',
      marginLeft: 10,
      marginTop: 5,
      left: 40,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#D8383D',
      borderRadius: 30,
      shadowColor: 'black',
      shadowOffset: {width: 4, height: 9},
      shadowOpacity: 0.1,
      elevation: 7,
      height: 60,
      width: 60,
    },
    btnImegContainerPlus: {
      position: 'absolute',
      marginLeft: 10,
      marginTop: 5,
      bottom: 10,
      right:10,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#748FFC',
      borderRadius: 30,
      shadowColor: 'black',
      shadowOffset: {width: 4, height: 9},
      shadowOpacity: 0.1,
      elevation: 7,
      height: 60,
      width: 60,
    },
    btnsImegContainer: {
      justifyContent: 'flex-start',
      alignContent: 'flex-start',
      flexDirection: 'row',
      width: '100%',
    }
})
